import telebot
from config import TOKEN
from telebot.types import ReplyKeyboardMarkup, KeyboardButton, InlineKeyboardMarkup, InlineKeyboardButton

bot = telebot.TeleBot(TOKEN)

def make_ketboard():
    keyboard = ReplyKeyboardMarkup(resize_keyboard=True, one_time_keyboard=False)
    button1 = KeyboardButton('/start')
    keyboard.add(button1,)
    return keyboard

def make_inline_keyboard1():
    keyboard = InlineKeyboardMarkup(row_width=2)
    button1 = InlineKeyboardButton('50000-100000', callback_data='1pr')
    button2 = InlineKeyboardButton('100000-250000', callback_data='2pr')
    button3 = InlineKeyboardButton('250000-500000', callback_data='3pr')
    button4 = InlineKeyboardButton('500000 and more', callback_data='4pr')
    keyboard.add(button1, button2,button3,button4)
    return keyboard


